local PANEL = {}function PANEL:SetCount(count)	self.Count = count	self.ShowCount = trueendfunction PANEL:SetItem(item)	if not item then		self.Entity = nil		self.Item = nil		return	end	self.Item = item	self:SetModel(item.InventoryModel)	self.Entity:SetAngles(item.InventoryAngles or Angle(0, 45, 20))	self.Entity:SetPos(item.InventoryPosition or vector_origin)	self.Entity:SetSkin(item.InventorySkin or 0)	self:SetViewDistance(item.InventoryDistance)	self:SetOutline(1.07)endfunction PANEL:HideCount()	self.ShowCount = falseendfunction PANEL:Paint(w, h)	if self.ShowCount and self.Item == inventory.Equipped() then		draw.RoundedBox(8, 3, 4, w - 8, h - 8, color_yellow_transparent)	end	self.BaseClass.Paint(self)	if self.ShowCount then		local text = string.format("x%d", self.Count or 1)		surface.SetFont("Zing22")		local tw, th = surface.GetTextSize(text)		DisableClipping(true)		-- draw text		draw.SimpleTextOutlined(text, "Zing22", w - tw + 2, h - th, color_white, TEXT_ALIGN_LEFT, TEXT_ALIGN_TOP, 1, color_black)		DisableClipping(false)	endendderma.DefineControl("InventoryIcon", "", PANEL, "3DIcon")